function Countdown(times){
	/*倒计时时长*/
	this.secondsDiff = times;
}
/*获取倒计时 时分秒位置*/
Countdown.prototype.getTimeEle = function($hourEle,$minEle,$secEle){
	this.timeEle = {
		hourEle : $hourEle,
		minEle : $minEle,
		secEle : $secEle
	};
	return this.timeEle;
}
/*开启定时器*/
Countdown.prototype.timer = function(){
	var _this = this;
	var timer = window.setInterval(function(){
		var day=0,
			hour=0,
			minute=0,
			second=0;//时间默认值		
		if(_this.secondsDiff > 0){
			hour = Math.floor(_this.secondsDiff / (60 * 60)) - (day * 24);
			minute = Math.floor(_this.secondsDiff / 60) - (day * 24 * 60) - (hour * 60);
			second = Math.floor(_this.secondsDiff) - (day * 24 * 60 * 60) - (hour * 60 * 60) - (minute * 60);
		}
		if (minute <= 9) minute = '0' + minute;
		if (second <= 9) second = '0' + second;
		_this.timeEle.hourEle.html('<span>'+hour+'</span>');
		_this.timeEle.minEle.html('<span>'+minute+'</span>');
		_this.timeEle.secEle.html('<span>'+second+'</span>');
		_this.secondsDiff--;
		if (_this.secondsDiff<0) {
			clearInterval(timer);
		}
	}, 1000);
}